<style>
    .slide-award {
        height: 267px;
        margin: auto;
        overflow: hidden;
        position: relative;
    }
    .slide-track-award {
        display: flex;
        gap: 33px;
        position: relative;
        transition: transform 0.5s ease-in-out;
        animation: award 100s linear infinite;
    }
    .slide-award-item {
        min-width: 200px;
        padding: 20px;
        text-align: center;
    }
    .text-content {
        font-size: 1.5rem;
        display: flex;
        align-items: center;
    }
    p img {
        width: 33px;
        margin-right: 10px;
    }

@keyframes award {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(-350%);
  }
}
.slide-track-award:hover {
  animation-play-state: paused;
}



